package kevin_harris_c09515259;

import java.math.*;
import java.util.*;

public class BlumBlumShub_Kevin_Harris_C09515259 {
	//big integers 
	private static BigInteger initial;
	private static BigInteger probablePrime;
	private static BigInteger prime1;
	private static BigInteger prime2;
	private static BigInteger seed;
	public static BigInteger result;
	public static BigInteger finalResult;
	
	public static void main(String args[]) {
		//initialise variables
		BlumBlumShub_Kevin_Harris_C09515259();
		BigInteger primeNumberCheck;
		for (int i = 0; i < 10; i++) {
			primeNumberCheck = generateRondomNumber();
			while (!isPrime(primeNumberCheck)) {
				primeNumberCheck = primeNumberCheck.add(new BigInteger("1001"));
				if (isPrime(primeNumberCheck)) {

					System.out.println("Prime Number " + i + " : "
							+ primeNumberCheck);
				}
			}
		}
		//check public key
		String pub = "";
		BigInteger bi = new BigInteger(pub);
		if (isPrime(bi)) {
			System.out.println("public key is prime");

		} else {

			System.out.println("public key is not prime");
		}
	}

	public static void BlumBlumShub_Kevin_Harris_C09515259() {
		Random rnd1 = new Random(new Date().getTime());
		initial = BigInteger.probablePrime(1024, rnd1);
		probablePrime = initial;
		// generate two prime number that should equal 3 when divided
		// by 4
		while (true) {
			probablePrime = probablePrime.nextProbablePrime();

			if ((probablePrime.mod(new BigInteger("4")).equals(new BigInteger(
					"3"))))
				break;
		}

		prime1 = probablePrime;

		while (true) {
			prime2 = probablePrime.nextProbablePrime();

			if ((prime2.mod(new BigInteger("4"))).equals(new BigInteger("3")))
				break;
		}

		// result = prime1 * prime2
		result = prime1.multiply(prime2);

		// generate a random number, the seed, that is relatively prime to the
		// result
		while (true) {
			rnd1 = new Random(new Date().getTime());
			seed = new BigInteger(2048, rnd1);
			if (seed.gcd(result).equals(BigInteger.ONE))
				break;
		}

		finalResult = (seed.multiply(seed)).mod(result);
	}

	public static BigInteger generateRondomNumber() {
		//multiply calculate Xo
		BigInteger x = (finalResult.multiply(finalResult)).mod(result);
		//set the finalResult to X(n-1)
		finalResult = x;
		return x;
	}

	public static boolean isPrime(BigInteger bigCheck) {
		if (bigCheck.isProbablePrime(1)) {
			return true;
		} else {
			return false;
		}
	}
}
